mapper.update:
UPDATE 	product
SET 		name=#{name}, code=#{code}, price=#{price}, brand=#{brand}, content=#{content}, imgSrc=#{imgSrc}, 
				filtercode=#{filterCode}, startdate=#{startDate}, enddate=#{endDate}, popularity=#{popularity}, 
				insertdate=#{insertDate}, flag_show=#{flag_show}, flag_del=#{flag_del}
WHERE 	code=#{code}
