mapper.select:
SELECT *
FROM raffle
WHERE productId=#{productId} AND status = #{status}

SELECT *
FROM raffle
WHERE productId=#{productId} AND (status='active' OR status='ready') AND delivery=#{deliveryType}

SELECT status
FROM raffle
WHERE id = #{id}

mapper.update:
UPDATE 	raffle
SET		status=#{status}
WHERE	id=#{id}
